import axios from "axios";

export const addStudent = async (student) => {
  const response = await axios.post("https://localhost:8000/addstudent", student);
  return response.data;
};

export const viewStudent = async () => {
  const response = await axios.get("https://localhost:8000/");
  return response.data;
};

export const deleteStudent = async (id) => {
  const response = await axios.delete(`https://localhost:8000/delete/${id}`);
  return response.data;
};

export const updateStudent = async (id, student) => {
  const response = await axios.put(
    `https://localhost:8000/update/${id}`,
    student
  );
  return response.data;
};
