import React, { useContext, useState } from "react";
// import {NavbarContext} from "../../context/Navabr/NavbarContext";

const Sidenavbar = () => {
  //   const { active} = useContext(NavbarContext);

  const [active, setActive] = useState(false);

  const handleActive = () => {
    setActive(!active);
  };

  const Navbar_item = [
    {
        icon: "fa fa-user fa-2x",
        text: "Add Student",
        active: true,
    },
    {
        icon: "fa fa-bars fa-2x text-textColor",
        text: "Manage Students",
        active: false,
    },
    {
        icon: "fa fa-sign-out fa-2x text-textColor",
        text: "Logout",
        active: false,
    },
    ];


  return (
    <div className="sidenavbar">
      <div className="sidenavbar__container flex flex-col w-[240px] ">
        {Navbar_item.map((item) => (
            <div
            className={`sidenavbar__container__item flex  items-center h-[48px] cursor-pointer rounded-md ${
                item.active ? "bg-[#fff] text-textColor" : "bg-[#F33823] text-[#fff]"
            }`}
            onClick={handleActive}
            >
            <div className="flex items-center ">
                <div className="sidenavbar__container__item__icon px-6">
                <i class={item.icon} aria-hidden="true"></i>
                </div>
                <div className="sidenavbar__container__item__text">
                <p className="font-normal text-base  font-IBM">
                    {item.text}
                </p>
                </div>
            </div>
            </div>
        ))

            }
        <div
          className={`sidenavbar__container__item flex  items-center h-[48px] cursor-pointer rounded-md ${
            active ? "bg-[#fff] text-textColor" : "bg-[#F33823] text-[#fff]"
          }`}
          onClick={handleActive}
        >
          <div className="flex items-center ">
            <div className="sidenavbar__container__item__icon px-6">
              <i class="fa fa-user fa-2x " aria-hidden="true"></i>
            </div>
            <div className="sidenavbar__container__item__text">
              <p className="font-normal text-base  font-IBM">
                Add Student
              </p>
            </div>
          </div>
        </div>
        <div className="sidenavbar__container__item flex  items-center h-[48px]">
          <div className="flex items-center ">
            <div className="sidenavbar__container__item__icon px-6">
              <i class="fa fa-bars fa-2x text-textColor" aria-hidden="true"></i>
            </div>
            <div className="sidenavbar__container__item__text">
              <p className="font-normal text-base text-textColor font-IBM">
                Manage Students
              </p>
            </div>
          </div>
        </div>
        {/* <div className="sidenavbar__container__item flex  items-center h-[48px]">
          <div className="flex items-center ">
            <div className="sidenavbar__container__item__icon px-6">
              <i
                class="fa fa-sign-out fa-2x text-textColor"
                aria-hidden="true"
              ></i>
            </div>
            <div className="sidenavbar__container__item__text">
              <p className="font-normal text-base text-textColor font-IBM">
                Logout
              </p>
            </div>
          </div>
        </div> */}
      </div>
    </div>
  );
};

export default Sidenavbar;
